import { BrowserRouter,Routes,Route,NavLink } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars,faXmark } from '@fortawesome/free-solid-svg-icons';
import { useEffect,useState } from "react";
import {useSelector,useDispatch} from 'react-redux'
import RegisterPage from "./components/register/register";
import LoginPage from "./components/login/login";
import AddTask from "./components/add-task/addTask";
import HomePage from "./components/home/homepage";
import "./App.css"
function App() {

  //navbar code
  //navbar icon code

  let [iconValue,handleIcon]=useState({
    initialValue:faBars
  })
  let handleClick=()=>{
    let navBox=document.querySelector(".navigation nav")
    handleIcon(()=>{
      if(iconValue.initialValue===faBars){
        return{
          initialValue:faXmark
        }
      }else{
        return{
          initialValue:faBars
        }
      }
    })
  }
  return (
    <div className="App">
      <BrowserRouter>
      <div className="navigation">
        <h1>Manage</h1>
        <FontAwesomeIcon icon={iconValue.initialValue} onClick={handleClick} className="hamburger-icon"/>
        <nav>
          <NavLink className="navLinks" to="/">Home</NavLink>
          <NavLink className="navLinks" to="/add-task">Add-Task</NavLink>
          <NavLink className="navLinks" to="/login">Login</NavLink>
        </nav>
      </div>
      <Routes>
        <Route path="/" element={<HomePage />}></Route>
        <Route path="/login" element={<LoginPage />}></Route>
        <Route path="/register" element={<RegisterPage />}></Route>
        <Route path="/add-task" element={<AddTask />}></Route>
      </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
